<script>


$(document).ready( function () {
	$("p").hide();   

 
    $("h2").click( function () {
		if( $(this).next("p").is(':visible') ){
			$(this).next("p").slideUp("normal");
 			$(this).children("img").attr("src","style/expand_orange.png");
		}	
		else{
			$(this).next("p").slideDown("normal");
			$(this).children("img").attr("src","style/reduire_orange.png");
		}
    });
	
});
</script>
